// src/components/ViolationCard.tsx
import React from 'react';
import { View, Text, Pressable, Image, StyleSheet } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';

type Item = {
  id: string;
  // parking fields
  licensePlate?: string;
  vehicleOwner?: string;
  spotOwner?: string;
  plotNumber?: string;
  imageUrl?: string;
  reportedAt?: string;
  // water fields
  location?: string;

  approved: boolean;
  approvedAt?: string;
};

interface Props {
  item: Item;
  expanded: boolean;
  onToggle: (id: string) => void;
  onApprove: (id: string) => void;
  approvingId?: string | null;
}

export default function ViolationCard({ item, expanded, onToggle, onApprove, approvingId }: Props) {
  const title = item.licensePlate ?? item.location ?? '—';
  const subtitle = item.vehicleOwner ?? item.reportedAt ?? item.spotOwner ?? '';
  const iconName = item.licensePlate ? 'car' : 'water';

  return (
    <View style={[
      styles.card,
      item.approved ? styles.cardApproved : styles.cardPending,
      expanded ? styles.cardExpanded : null
    ]}>
      <Pressable onPress={() => onToggle(item.id)} style={styles.row}>
        <View style={styles.left}>
          <View style={[styles.iconWrap, item.approved ? styles.iconApprovedBG : styles.iconPendingBG]}>
            <MaterialCommunityIcons name={iconName as any} size={22} color={item.approved ? '#065f46' : '#991b1b'} />
          </View>
        </View>

        <View style={styles.mid}>
          <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between' }}>
            <View style={{ flex: 1 }}>
              <Text style={styles.title} numberOfLines={1}>{title}</Text>
            </View>
            <View style={[styles.statusChip, item.approved ? styles.statusApproved : styles.statusPending]}>
              <Text style={[styles.statusText, item.approved ? styles.statusTextApproved : styles.statusTextPending]}>
                {item.approved ? 'Approved' : 'Pending'}
              </Text>
            </View>
          </View>

          <View style={{ marginTop: 8 }}>
            {item.vehicleOwner ? (
              <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                <MaterialCommunityIcons name="account" size={14} color="#6b7280" style={{ marginRight: 6 }} />
                <Text style={styles.sub}>{item.vehicleOwner}</Text>
              </View>
            ) : null}

            {item.reportedAt ? (
              <View style={{ flexDirection: 'row', alignItems: 'center', marginTop: 6 }}>
                <MaterialCommunityIcons name="calendar" size={14} color="#6b7280" style={{ marginRight: 6 }} />
                <Text style={[styles.sub]}>{item.reportedAt}</Text>
              </View>
            ) : null}
          </View>
        </View>
      </Pressable>

      {expanded && (
        <View style={styles.expanded}>
          {/* details */}
          {item.spotOwner ? (
            <View style={styles.detailRow}>
              <Text style={{ color: '#6b7280', fontSize: 12 }}>Spot Owner</Text>
              <Text style={{ color: '#0f172a', marginTop: 6 }}>{item.spotOwner}</Text>
            </View>
          ) : null}

          {item.plotNumber ? (
            <View style={styles.detailRow}>
              <Text style={{ color: '#6b7280', fontSize: 12 }}>Plot Number</Text>
              <Text style={{ color: '#0f172a', marginTop: 6 }}>{item.plotNumber}</Text>
            </View>
          ) : null}

          {item.approved && item.approvedAt ? (
            <Text style={{ color: '#065f46', marginTop: 8 }}>Approved on {item.approvedAt}</Text>
          ) : null}

          {!item.approved && (
            <Pressable
              onPress={() => onApprove(item.id)}
              disabled={approvingId === item.id}
              style={({pressed}) => [
                {
                  backgroundColor: pressed ? '#1e40af' : '#2563eb',
                },
                styles.approveBtn
              ]}
            >
              <MaterialCommunityIcons name="check-circle-outline" size={18} color="#fff" style={{ marginRight: 8 }} />
              <Text style={{ color: '#fff', fontWeight: '700' }}>{approvingId === item.id ? 'Approving...' : 'Approve'}</Text>
            </Pressable>
          )}
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#fff',
    borderRadius: 12,
    borderWidth: 2,
    paddingVertical: 12,
    paddingHorizontal: 14,
    marginBottom: 14,
  },
  cardApproved: {
    borderColor: '#bbf7d0',
    backgroundColor: 'rgba(220, 253, 231, 0.4)',
  },
  cardPending: {
    borderColor: '#fee2e2',
    backgroundColor: 'rgba(254, 242, 242, 0.35)',
  },
  cardExpanded: {
    shadowColor: '#000',
    shadowOpacity: 0.06,
    shadowRadius: 10,
    elevation: 3
  },
  row: { flexDirection: 'row', alignItems: 'flex-start' },
  left: { width: 52, justifyContent: 'center', alignItems: 'center' },
  mid: { flex: 1, paddingLeft: 6 },
  iconWrap: { width: 44, height: 44, borderRadius: 10, justifyContent: 'center', alignItems: 'center' },
  iconApprovedBG: { backgroundColor: '#d1fae5' },
  iconPendingBG: { backgroundColor: '#fee2e2' },
  title: { fontSize: 16, fontWeight: '700', color: '#0f172a' },
  sub: { color: '#6b7280', fontSize: 14 },
  statusChip: { paddingHorizontal: 12, paddingVertical: 6, borderRadius: 999 },
  statusApproved: { backgroundColor: '#ecfdf5', borderWidth: 1, borderColor: '#bbf7d0' },
  statusPending: { backgroundColor: '#fff1f2', borderWidth: 1, borderColor: '#fecaca' },
  statusText: { fontSize: 12, fontWeight: '700' },
  statusTextApproved: { color: '#065f46' },
  statusTextPending: { color: '#991b1b' },
  expanded: { marginTop: 12, paddingTop: 12, borderTopWidth: 1, borderTopColor: '#eef2f6' },
  detailRow: { backgroundColor: '#f8fafc', padding: 12, borderRadius: 8, marginBottom: 8 }
});
