import React, { useState } from 'react';
import { View, Text, TextInput, Pressable, Image } from 'react-native';
import { collection, getDocs, query, where } from 'firebase/firestore';
import { db } from '../firebase';
import ParkingScreen from './ParkingScreen';

export function LoginScreen() {
  const [email, setEmail] = useState('test');
  const [password, setPassword] = useState('123');
  const [loggedIn, setLoggedIn] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleLogin = async () => {
    setError(null);
    try {
      const usersRef = collection(db, 'users');
      const q = query(usersRef, where('name', '==', email), where('password', '==', password));
      const snap = await getDocs(q);
      if (!snap.empty) {
        setLoggedIn(true);
      } else {
        setError('Invalid credentials (check Firestore users collection).');
      }
    } catch (err) {
      setError('Firebase error - check console.');
      console.error(err);
    }
  };

  if (loggedIn) {
    return <ParkingScreen />;
  }

  return (
    <View style={{ flex: 1, justifyContent: 'center', padding: 20 }}>
      <View style={{ alignItems: 'center', marginBottom: 24 }}>
        <Image source={require('../../assets/e080a70e-6b98-49df-a8fe-41c614e8950b.png')} style={{ width: 96, height: 96, borderRadius: 48 }} />
      </View>
      <Text style={{ fontSize: 22, fontWeight: '600', textAlign: 'center', marginBottom: 8 }}>Campus Sentry</Text>
      <Text style={{ textAlign: 'center', color: '#6b7280', marginBottom: 20 }}>Sign in to your dashboard</Text>

      <TextInput value={email} onChangeText={setEmail} placeholder='username' style={{ borderWidth: 1, borderColor: '#d1d5db', padding: 12, borderRadius: 8, marginBottom: 12 }} />
      <TextInput value={password} onChangeText={setPassword} placeholder='password' secureTextEntry style={{ borderWidth: 1, borderColor: '#d1d5db', padding: 12, borderRadius: 8, marginBottom: 12 }} />

      {error ? <Text style={{ color: '#dc2626', marginBottom: 12 }}>{error}</Text> : null}

      <Pressable onPress={handleLogin} style={{ backgroundColor: '#2563eb', padding: 12, borderRadius: 8, alignItems: 'center' }}>
        <Text style={{ color: '#fff', fontWeight: '600' }}>Sign In</Text>
      </Pressable>
    </View>
  );
}

export default LoginScreen;
