// src/screens/DashboardScreen.tsx
import React, { useState } from 'react';
import { View, Text, Pressable, ScrollView } from 'react-native';
import ParkingScreen from './ParkingScreen';
import WaterLoggingScreen from './WaterLoggingScreen';

interface Props {
  user: { name: string; email: string };
  onSignOut: () => void;
}

export default function DashboardScreen({ user, onSignOut }: Props) {
  const [tab, setTab] = useState<'parking'|'water'>('parking');

  return (
    <View style={{ flex: 1, backgroundColor: '#f8fafc' }}>
      <View style={{ backgroundColor: '#fff', padding: 16, borderBottomWidth: 1, borderColor: '#e6e7eb' }}>
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
          <View>
            <Text style={{ fontSize: 24, fontWeight: '800', color: '#1e40af' }}>Campus Sentry</Text>
            <Text style={{ color: '#94a3b8' }}>Dashboard Overview</Text>
          </View>

          <View style={{ alignItems: 'flex-end' }}>
            <Text style={{ fontWeight: '700' }}>{user?.name}</Text>
            <Text style={{ color: '#6b7280' }}>{user?.email}</Text>
            <Pressable onPress={onSignOut} style={{ marginTop: 8, borderWidth: 1, borderColor: '#fecaca', paddingHorizontal: 10, paddingVertical: 6, borderRadius: 8 }}>
              <Text style={{ color: '#b91c1c' }}>Sign Out</Text>
            </Pressable>
          </View>
        </View>
      </View>

      <View style={{ backgroundColor: '#fff', borderBottomWidth: 1, borderColor: '#e6e7eb' }}>
        <View style={{ flexDirection: 'row' }}>
          <Pressable onPress={() => setTab('parking')} style={{ flex: 1, padding: 14, alignItems: 'center', borderBottomWidth: tab === 'parking' ? 3 : 0, borderBottomColor: '#2563eb' }}>
            <Text style={{ color: tab === 'parking' ? '#2563eb' : '#6b7280', fontWeight: tab === 'parking' ? '700' : '600' }}>Parking Violations</Text>
          </Pressable>
          <Pressable onPress={() => setTab('water')} style={{ flex: 1, padding: 14, alignItems: 'center', borderBottomWidth: tab === 'water' ? 3 : 0, borderBottomColor: '#2563eb' }}>
            <Text style={{ color: tab === 'water' ? '#2563eb' : '#6b7280', fontWeight: tab === 'water' ? '700' : '600' }}>Water Logging</Text>
          </Pressable>
        </View>
      </View>

      <View style={{ flex: 1 }}>
        {tab === 'parking' ? <ParkingScreen /> : <WaterLoggingScreen />}
      </View>
    </View>
  );
}
