// src/components/ViolationCard.tsx
import React from 'react';
import { View, Text, Pressable } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';

interface Props {
  item: any;
  expanded: boolean;
  onToggle: (id: string) => void;
  onApprove: (id: string) => void;
  approvingId?: string | null;
}

export default function ViolationCard({ item, expanded, onToggle, onApprove, approvingId }: Props) {
  return (
    <View style={{
      backgroundColor: '#fff',
      borderRadius: 12,
      borderWidth: 2,
      borderColor: item.approved ? '#bbf7d0' : '#fecaca',
      marginBottom: 12,
      overflow: 'hidden',
      shadowColor: '#000',
      shadowOpacity: 0.06,
      shadowRadius: 10,
      elevation: 3
    }}>
      <Pressable onPress={() => onToggle(item.id)} style={{ padding: 14 }}>
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
          <View style={{ flexDirection: 'row', alignItems: 'center' }}>
            <View style={{
              width: 48, height: 48, borderRadius: 10, justifyContent: 'center', alignItems: 'center',
              backgroundColor: item.approved ? '#dcfce7' : '#fee2e2', marginRight: 12
            }}>
              <MaterialIcons name="directions-car" size={22} color={item.approved ? '#059669' : '#b91c1c'} />
            </View>
            <View style={{ maxWidth: 220 }}>
              <Text style={{ fontWeight: '700', color: '#0f172a' }}>{item.licensePlate || '—'}</Text>
              <Text style={{ color: '#6b7280', marginTop: 4 }}>{item.vehicleOwner || '—'}</Text>
            </View>
          </View>

          <View>
            <View style={{
              backgroundColor: item.approved ? '#d1fae5' : '#fee2e2',
              paddingHorizontal: 10, paddingVertical: 6, borderRadius: 999
            }}>
              <Text style={{ color: item.approved ? '#065f46' : '#991b1b', fontWeight: '600', fontSize: 12 }}>
                {item.approved ? 'Approved' : 'Pending'}
              </Text>
            </View>
          </View>
        </View>
      </Pressable>

      {expanded && (
        <View style={{ padding: 14, borderTopWidth: 1, borderColor: '#eef2f7' }}>
          <View style={{ backgroundColor: '#f8fafc', padding: 12, borderRadius: 8, marginBottom: 10 }}>
            <Text style={{ color: '#94a3b8', fontSize: 12 }}>Spot Owner</Text>
            <Text style={{ color: '#0f172a', marginTop: 6 }}>{item.spotOwner || '—'}</Text>
          </View>

          <View style={{ backgroundColor: '#f8fafc', padding: 12, borderRadius: 8, marginBottom: 12 }}>
            <Text style={{ color: '#94a3b8', fontSize: 12 }}>Plot Number</Text>
            <Text style={{ color: '#0f172a', marginTop: 6 }}>{item.plotNumber || '—'}</Text>
          </View>

          {!item.approved ? (
            <Pressable
              onPress={() => onApprove(item.id)}
              disabled={approvingId === item.id}
              style={{ backgroundColor: '#2563eb', padding: 12, borderRadius: 10, alignItems: 'center' }}>
              <Text style={{ color: '#fff', fontWeight: '700' }}>
                {approvingId === item.id ? 'Approving...' : 'Approve Violation'}
              </Text>
            </Pressable>
          ) : (
            <Text style={{ color: '#065f46', fontWeight: '600' }}>Approved</Text>
          )}
        </View>
      )}
    </View>
  );
}
